query endpointVulnerabilityCVSSv2($incidentHosts: EndpointFieldFilter, $endCursor: Cursor, $source: EndpointSource) {
	endpoints(source: $source, first: 10, after: $endCursor, filter: $incidentHosts) {
	  edges {
		node {
		  id
		  name
		  ipAddress
		  compliance {
			cveFindings (
			filter: {any: false, filters: [{any:true,path:"cveId",op:CONTAINS,value:"-"}]}
			){
			  cveId
			  cvssScoreV3
			  lastFound
			  severity
			  summary
			}
		  }
		}
	  }
	  pageInfo {
		startCursor
		endCursor
		hasPreviousPage
		hasNextPage
	  }
	}
  }

